diff:
"27308d9 bug 396307 - Preference bindings don't report exceptions in user-defined event handlers to the Error Console fix by switching from dump()ing the exception in the catch clause to using Components.utils.reportError() r=gavin, a=mconnor
diff --git a/toolkit/content/widgets/preferences.xml b/toolkit/content/widgets/preferences.xml
index 6a95aae..2ded470 100644
--- a/toolkit/content/widgets/preferences.xml
+++ b/toolkit/content/widgets/preferences.xml
@@ -52,7 +52,7 @@
             aPreference.dispatchEvent(event);
           }
           catch (e) {
-            dump(e);
+            Components.utils.reportError(e);
           }
         ]]>
         </body>
@@ -373,7 +373,7 @@
               rv = f(event);
             }
             catch (e) {
-              dump(e);
+              Components.utils.reportError(e);
             }
           }
           var val = rv !== undefined ? rv : (this.instantApply ? this.valueFromPreferences : this.value);
@@ -424,7 +424,7 @@
                 return rv;
             }
             catch (e) {
-              dump(e);
+              Components.utils.reportError(e);
             }
           }

"
