@@ -205,8 +205,9 @@ var PlacesCommandHook = {
     // it to the content area
     var starIcon = document.getElementById("star-button");
     if (starIcon && isElementVisible(starIcon)) {
+      var dockTo = starIcon.parentNode; // urlbar endcap
       this.bookmarkPage(getBrowser().selectedBrowser, aParent, aShowEditUI,
-                        starIcon, "after_start");
+                        dockTo, "after_start");
     }
     else {
       this.bookmarkPage(getBrowser().selectedBrowser, aParent, aShowEditUI,
diff --git a/browser/base/content/browser.js b/browser/base/content/browser.js
index 63de19b..7b1a175 100644
--- a/browser/base/content/browser.js
+++ b/browser/base/content/browser.js
@@ -956,9 +956,9 @@ function delayedStartup()
   if (gURLBar && document.documentElement.getAttribute("chromehidden").indexOf("toolbar") != -1) {
     gURLBar.setAttribute("readonly", "true");
     gURLBar.setAttribute("enablehistory", "false");
-    var goButtonStack = document.getElementById("go-button-stack");
-    if (goButtonStack)
-      goButtonStack.setAttribute("hidden", "true");
+    var urlbarEndcap = document.getElementById("urlbar-endcap");
+    if (urlbarEndcap)
+      urlbarEndcap.setAttribute("hidden", "true");
   }

   if (gURLBar) {
@@ -5574,9 +5574,9 @@ function showToolbars() {
     gURLBar.setAttribute("enablehistory", "true");
   }

-  var goButtonStack = document.getElementById("go-button-stack");
-  if (goButtonStack)
-    goButtonStack.removeAttribute("hidden");
+  var urlbarEndcap = document.getElementById("urlbar-endcap");
+  if (urlbarEndcap)
+    urlbarEndcap.removeAttribute("hidden");

   return false; // Dismiss the notification message
 }
diff --git a/browser/base/content/browser.xul b/browser/base/content/browser.xul
index 0dd2062..70ea737 100644
--- a/browser/base/content/browser.xul
+++ b/browser/base/content/browser.xul
@@ -264,20 +264,17 @@
 #endif
             </hbox>
           </textbox>
-          <stack id="go-button-stack" class="endcap">
-            <box class="endcap-box" chromedir="&locale.dir;"/>
-            <hbox>
-              <toolbarbutton id="star-button"
-                             onclick="if (event.button == 0) PlacesStarButton.onClick(event);"/>
-              <toolbarbutton id="go-button" chromedir="&locale.dir;"
-                             label="&goEndCap.label;"
-                             onclick="handleURLBarCommand(event);"
-                             ondragover="nsDragAndDrop.dragOver(event, goButtonObserver);"
-                             ondragdrop="nsDragAndDrop.drop(event, goButtonObserver);"
-                             ondragexit="nsDragAndDrop.dragExit(event, goButtonObserver);"
-                             tooltiptext="&goEndCap.tooltip;"/>
-            </hbox>
-          </stack>
+          <hbox id="urlbar-endcap" class="endcap" chromedir="&locale.dir;">
+            <toolbarbutton id="star-button"
+                           onclick="if (event.button == 0) PlacesStarButton.onClick(event);"/>
+            <toolbarbutton id="go-button" chromedir="&locale.dir;"
+                           label="&goEndCap.label;"
+                           onclick="handleURLBarCommand(event);"
+                           ondragover="nsDragAndDrop.dragOver(event, goButtonObserver);"
+                           ondragdrop="nsDragAndDrop.drop(event, goButtonObserver);"
+                           ondragexit="nsDragAndDrop.dragExit(event, goButtonObserver);"
+                           tooltiptext="&goEndCap.tooltip;"/>
+          </hbox>
         </hbox>
       </toolbaritem>

