diff:
"7b3fcc8 Bug 372773: Tab favicon should have the grab cursor on hover. p=Dao Gottwald <dao@design-noir.de> r=enndeakin, ui-r=beltzner
diff --git a/toolkit/themes/pinstripe/global/browser.css b/toolkit/themes/pinstripe/global/browser.css
index 4aa9904..da98a66 100644
--- a/toolkit/themes/pinstripe/global/browser.css
+++ b/toolkit/themes/pinstripe/global/browser.css
@@ -47,6 +47,10 @@
   -moz-user-focus: normal;
 }

+.tabbrowser-tab[selected="true"] > .tab-image-middle > .tab-icon {
+  cursor: -moz-grab;
+}
+
 .tabbrowser-tab > .tab-image-middle > .tab-text {
   border: 1.4pt solid transparent;
 }
diff --git a/toolkit/themes/winstripe/global/browser.css b/toolkit/themes/winstripe/global/browser.css
index f62d253..64db7cb 100644
--- a/toolkit/themes/winstripe/global/browser.css
+++ b/toolkit/themes/winstripe/global/browser.css
@@ -44,8 +44,8 @@
   font-weight: bold;
 }

-.tabbrowser-tab[selected="true"] > .tab-image-middle > .tab-text {
-  opacity: 1.0 !important;
+.tabbrowser-tab[selected="true"] > .tab-image-middle > .tab-icon {
+  cursor: -moz-grab;
 }

 .tabbrowser-tab > .tab-image-left {
@@ -205,13 +205,11 @@ tabpanels {
 .tab-close-button:hover,
 .tabbrowser-tab[selected="true"] > .tab-close-button:hover {
   -moz-image-region: rect(0px, 32px, 16px, 16px);
-  opacity: 1.0;
 }

 .tab-close-button:hover:active,
 .tabbrowser-tab[selected="true"] > .tab-close-button:hover:active {
   -moz-image-region: rect(0px, 48px, 16px, 32px);
-  opacity: 1.0;
 }

 .tabbrowser-tab > .tab-close-button {
"
