diff:
"8099f15 Bug 372773: Tab favicon should have the grab cursor on hover (right files this time). p=Dao Gottwald <dao@design-noir.de> r=enndeakin, ui-r=beltzner
diff --git a/browser/themes/pinstripe/browser/browser.css b/browser/themes/pinstripe/browser/browser.css
index 9103e1d..b506f30 100755
--- a/browser/themes/pinstripe/browser/browser.css
+++ b/browser/themes/pinstripe/browser/browser.css
@@ -1247,6 +1247,10 @@ toolbarbutton.chevron > .toolbarbutton-menu-dropmarker {
   -moz-user-focus: normal;
 }

+.tabbrowser-tab[selected="true"] > .tab-image-middle > .tab-icon {
+  cursor: -moz-grab;
+}
+
 .tabbrowser-tab > .tab-image-middle > .tab-text {
   border: 1.4pt solid transparent;
 }
diff --git a/browser/themes/winstripe/browser/browser.css b/browser/themes/winstripe/browser/browser.css
index a2df396..0c5f8a2 100644
--- a/browser/themes/winstripe/browser/browser.css
+++ b/browser/themes/winstripe/browser/browser.css
@@ -1271,8 +1271,8 @@ toolbar[mode="text"] #navigator-throbber[busy="true"] {
   font-weight: bold;
 }

-.tabbrowser-tab[selected="true"] > .tab-image-middle > .tab-text {
-  opacity: 1.0 !important;
+.tabbrowser-tab[selected="true"] > .tab-image-middle > .tab-icon {
+  cursor: -moz-grab;
 }

 .tabbrowser-tab > .tab-image-left {
@@ -1432,13 +1432,11 @@ tabpanels {
 .tab-close-button:hover,
 .tabbrowser-tab[selected="true"] > .tab-close-button:hover {
   -moz-image-region: rect(0px, 32px, 16px, 16px);
-  opacity: 1.0;
 }

 .tab-close-button:hover:active,
 .tabbrowser-tab[selected="true"] > .tab-close-button:hover:active {
   -moz-image-region: rect(0px, 48px, 16px, 32px);
-  opacity: 1.0;
 }

 .tabbrowser-tab > .tab-close-button {
"
