diff:
"a25daaa bug 396989: change 'Save to Disk' to 'Save File' in Applications prefpane; r=gavin, a=mconnor
diff --git a/browser/components/preferences/applications.js b/browser/components/preferences/applications.js
index a20412d..30936b9 100755
--- a/browser/components/preferences/applications.js
+++ b/browser/components/preferences/applications.js
@@ -1002,7 +1002,7 @@ var gApplicationsPane = {

     switch (aHandlerInfo.preferredAction) {
       case Ci.nsIHandlerInfo.saveToDisk:
-        return this._prefsBundle.getString("saveToDisk");
+        return this._prefsBundle.getString("saveFile");

       case Ci.nsIHandlerInfo.useHelperApp:
         return aHandlerInfo.preferredApplicationHandler.name;
@@ -1172,7 +1172,7 @@ var gApplicationsPane = {
         !handlerInfo.handledOnlyByPlugin) {
       let menuItem = document.createElementNS(kXULNS, "menuitem");
       menuItem.setAttribute("action", Ci.nsIHandlerInfo.saveToDisk);
-      menuItem.setAttribute("label", this._prefsBundle.getString("saveToDisk"));
+      menuItem.setAttribute("label", this._prefsBundle.getString("saveFile"));
       menuPopup.appendChild(menuItem);
       if (handlerInfo.preferredAction == Ci.nsIHandlerInfo.saveToDisk)
         menu.selectedItem = menuItem;
diff --git a/browser/locales/en-US/chrome/browser/preferences/preferences.properties b/browser/locales/en-US/chrome/browser/preferences/preferences.properties
index 20f4e14..79ae5ea 100644
--- a/browser/locales/en-US/chrome/browser/preferences/preferences.properties
+++ b/browser/locales/en-US/chrome/browser/preferences/preferences.properties
@@ -46,7 +46,7 @@ chooseDownloadFolderTitle=Choose Download Folder:
 #### Applications

 fileEnding=%S file
-saveToDisk=Save to Disk
+saveFile=Save File
 chooseApp=Choose application...
 fpTitleChooseApp=Select Helper Application
 webFeed=Web Feed
"
