+
+.alltabs-item[busy] > .menu-iconic-left > .menu-iconic-icon {
+  list-style-image: url("chrome://global/skin/icons/loading_16_grey.gif") !important;
+}

 .tabs-closebutton {
   padding-right: 4px;
