diff:
"eeedddf bug 379327 - have browser chrome tests print test filename in FAIL lines.  r=gavin
diff --git a/testing/mochitest/browser-harness.xul b/testing/mochitest/browser-harness.xul
index b4d481c..c7673a1 100644
--- a/testing/mochitest/browser-harness.xul
+++ b/testing/mochitest/browser-harness.xul
@@ -116,7 +116,12 @@
         return this.tests.filter(function (t) !t.pass).length;
       },
       get log() {
-        return this.tests.map(function (t) t.msg).join("\n");
+        var path = this.path;
+        return this.tests.map(function (t) {
+                                if (!t.pass)
+                                  return t.msg + " - " + path;
+                                return t.msg;
+                              }).join("\n");
       }
     };

"
