diff:
"f263d1e Bug 343904. Support STATE_FLOATING, patch by Surkov, r=aaronlev, a=dsicore
diff --git a/accessible/src/base/nsAccessible.cpp b/accessible/src/base/nsAccessible.cpp
index e9e3075..92965bd 100644
--- a/accessible/src/base/nsAccessible.cpp
+++ b/accessible/src/base/nsAccessible.cpp
@@ -1051,6 +1051,10 @@ nsAccessible::GetState(PRUint32 *aState, PRUint32 *aExtraState)
     *aState |= nsIAccessibleStates::STATE_OFFSCREEN;
   }

+  nsIFrame *frame = GetFrame();
+  if (frame && (frame->GetStateBits() & NS_FRAME_OUT_OF_FLOW))
+    *aState |= nsIAccessibleStates::STATE_FLOATING;
+
   return NS_OK;
 }

"
